export const fileSystem: "\n<!DOCTYPE html>\n<html lang=\"zh-CN\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>\u6587\u4EF6\u7CFB\u7EDF | File System</title>\n    <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css\">\n    <style>\n        * {\n            margin: 0;\n            padding: 0;\n            box-sizing: border-box;\n            transition: background-color 0.3s, color 0.3s;\n        }\n        body {\n            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;\n            line-height: 1.4;\n            padding: 1.5rem;\n        }\n        body.dark-mode {\n            background: #1a1a1a;\n            color: #e0e0e0;\n        }\n        .container {\n            max-width: 1200px;\n            margin: 0 auto;\n            padding: 0 1rem;\n        }\n        .header-container {\n            display: flex;\n            justify-content: space-between;\n            align-items: center;\n            margin-bottom: 1.5rem;\n            padding-bottom: 1rem;\n            border-bottom: 2px solid #eee;\n        }\n        .dark-mode .header-container {\n            border-bottom-color: #333;\n        }\n        h1 {\n            font-size: 1.8rem;\n            color: #2c3e50;\n            margin: 0;\n        }\n        .dark-mode h1 {\n            color: #e0e0e0;\n        }\n        .theme-toggle {\n            background: none;\n            border: none;\n            cursor: pointer;\n            font-size: 1.5rem;\n            color: #2c3e50;\n            padding: 0.5rem;\n        }\n        .dark-mode .theme-toggle {\n            color: #e0e0e0;\n        }\n        .file-list {\n            list-style: none;\n        }\n        .file-list li {\n            padding: 0.3rem 0.5rem;\n            transition: background-color 0.2s;\n            display: grid;\n            grid-template-columns: minmax(0, 1fr) auto;\n            align-items: center;\n            gap: 1rem;\n            height: 32px;\n            min-width: 0;\n        }\n        .file-list li:hover {\n            background-color: #f5f5f5;\n        }\n        .dark-mode .file-list li:hover {\n            background-color: #2d2d2d;\n        }\n        .file-list a {\n            display: flex;\n            align-items: center;\n            text-decoration: none;\n            color: #2c3e50;\n            height: 100%;\n            min-width: 0;\n            width: 100%;\n        }\n        .file-name {\n            display: flex;\n            white-space: nowrap;\n            min-width: 0;\n            max-width: 100%;\n        }\n        .file-name-main {\n            overflow: hidden;\n            text-overflow: ellipsis;\n            min-width: 10px;\n        }\n        .file-name-ext {\n            flex-shrink: 0;\n        }\n        .dark-mode .file-list a {\n            color: #e0e0e0;\n        }\n        .file-list i {\n            margin-right: 1rem;\n            font-size: 1.2rem;\n            line-height: 1;\n        }\n        .file-list .fa-folder {\n            color: #f8d775;\n        }\n        .file-list .fa-file {\n            color: #a0a0a0;\n        }\n        .dark-mode .file-list .fa-file {\n            color: #808080;\n        }\n        .download-btn {\n            color: #666;\n            cursor: pointer;\n            border: none;\n            background: none;\n            transition: color 0.2s;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            width: 2rem;\n            height: 100%;\n            padding: 0;\n        }\n        .dark-mode .download-btn {\n            color: #888;\n        }\n        .download-btn:hover {\n            color: #2c3e50;\n        }\n        .dark-mode .download-btn:hover {\n            color: #e0e0e0;\n        }\n        .parent-link {\n            margin-bottom: 1rem;\n            display: inline-block;\n            padding: 0.5rem 1rem;\n            background: #e9ecef;\n            border-radius: 4px;\n            text-decoration: none;\n            color: #495057;\n        }\n        .dark-mode .parent-link {\n            background: #2d2d2d;\n            color: #e0e0e0;\n        }\n        .parent-link:hover {\n            background: #dee2e6;\n        }\n        .dark-mode .parent-link:hover {\n            background: #3d3d3d;\n        }\n        @media (max-width: 768px) {\n            body {\n                padding: 1rem;\n            }\n            .container {\n                padding: 0.5rem;\n            }\n            h1 {\n                font-size: 1.2rem;\n            }\n            .file-list li {\n                padding: 0.3rem;\n                gap: 0.5rem;\n            }\n            .file-list i {\n                margin-right: 0.5rem;\n                font-size: 1rem;\n            }\n            .download-btn {\n                width: 1.5rem;\n            }\n        }\n    </style>\n</head>\n<body class=\"dark-mode\">\n    <div class=\"container\">\n        <div class=\"header-container\">\n            <h1>\u6587\u4EF6\u76EE\u5F55 Directory</h1>\n            <button class=\"theme-toggle\" aria-label=\"Toggle dark mode\">\n                <i class=\"fas fa-sun\"></i>\n            </button>\n        </div>\n        <script>\n            // \u4E3B\u9898\u5207\u6362\u529F\u80FD\n            const body = document.body;\n            const themeToggle = document.querySelector('.theme-toggle');\n            const themeIcon = themeToggle.querySelector('i');\n            // \u4ECE localStorage \u83B7\u53D6\u4E3B\u9898\u8BBE\u7F6E\n            const isDarkMode = localStorage.getItem('darkMode') !== 'false'; // \u9ED8\u8BA4\u4E3A\u6697\u8272\u4E3B\u9898\n            body.classList.toggle('dark-mode', isDarkMode);\n            updateThemeIcon(isDarkMode);\n            themeToggle.addEventListener('click', () => {\n                const isDark = body.classList.toggle('dark-mode');\n                localStorage.setItem('darkMode', isDark);\n                updateThemeIcon(isDark);\n            });\n            function updateThemeIcon(isDark) {\n                themeIcon.className = isDark ? 'fas fa-sun' : 'fas fa-moon';\n            }\n        </script>\n";
